import React, { useState } from "react";
import "./Form.css";

const Form = ({ addTodoToTodoList }) => {
  const [nameValue, setnameValue] = useState("");
  const [ageValue, setageValue] = useState("");
 


  
  const handleNameChange = (event) => {
    setnameValue(event.target.value);
   


  };
  const handleAgeChange = (event) => {
    setageValue(event.target.value);
   

  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!nameValue) return;
    addTodoToTodoList(nameValue,ageValue);
    setnameValue("");
    setageValue("");
    //addTodoToTodoList(typeValue);
    //settypeValue("");
    //addTodoToTodoList(imageValue);
    //setimageValue("");
  };

 
  return (
    <form onSubmit={handleSubmit} className="form-container"> 
      <input value={nameValue} onChange={handleNameChange} type="text" placeholder="name"/>
      <input value={ageValue} onChange={handleAgeChange} type="text" placeholder="age" />
     
      <button type="submit">Add</button>
    </form>


  );
};

export default Form;


