import React,{useState} from "react";

import Layout from '../layout/Layout'
import Form from '../components/Form/Form'
import TableList from '../components/TableList/TableList'

import "../style/global.css"
import{v4 as uuid} from "uuid";


const App = ()=> {
  const [todoList,setTodoList]=useState([
    {id: uuid() , task:"code some react",isCompleted:false},
    {id: uuid() ,task:"eat a lot of food",isCompleted:true},
    {id: uuid() , task:"drink 2 bottles of ark",isCompleted:false},
  ]);

  const addTodoToTodoList= (todo) =>{
   const newTodo={
    id:uuid(),
    task: todo,
    isCompleted:false,
   };
   setTodoList(prevTodoList => [...prevTodoList,newTodo]);
  };




  return (
    <Layout>
      <div className='app-main-container'>
      <Form addTodoToTodoList={addTodoToTodoList} />
      <TableList list={todoList} />
      </div>
    </Layout>
  )
}




export default App
