import React,{useState} from "react";

import Layout from '../layout/Layout'
import Form from '../components/Form/Form'
import TableList from '../components/TableList/TableList'

import "../style/global.css"
import{v4 as uuid} from "uuid";


const App = ()=> {
  const [todoList,setTodoList]=useState([
    {id: uuid() , name:"name a", age:"10", type:"lion", Image:"https://images.search.yahoo.com/search/images;_ylt=AwrJ_Hto429j9EIeHitXNyoA;_ylu=Y29sbwNiZjEEcG9zAzEEdnRpZANMT0NVSTA1M0NfMQRzZWMDc2M-?p=lion&type=E211US714G0&ei=UTF-8&fr=mcafee&th=114.4&tw=171.1&imgurl=https%3A%2F%2Fwww.awsfzoo.com%2Fmedia%2FDSC_0339.jpg&rurl=https%3A%2F%2Fwww.awsfzoo.com%2Fexhibit%2Fafrican-lion%2F&size=762KB&name=White+Lion+-+Animal+World+and+Snake+Farm&oid=3&h=2592&w=3872&turl=https%3A%2F%2Ftse1.mm.bing.net%2Fth%3Fid%3DOIP.Oib-MHqWN24cNLRp03WOuwHaE9%26pid%3DApi%26rs%3D1%26c%3D1%26qlt%3D95%26w%3D171%26h%3D114&tt=White+Lion+-+Animal+World+and+Snake+Farm&sigr=F.pLQ_W6V.GF&sigit=DEp7DGYo9eVS&sigi=WQ2b8dJyJ0h1&sign=grgzO7yAIvVu&sigt=grgzO7yAIvVu"},
    {id: uuid() , name:"name b"},
    {id: uuid() , name:"name c"},
  ]);

  const addTodoToTodoList= (todo) =>{
   const newTodo={
    id:uuid(),
    task: todo,
    isCompleted:false,
   };
   setTodoList(prevTodoList => [...prevTodoList,newTodo]);
  };




  return (
    <Layout>
      <div className='app-main-container'>
      <Form addTodoToTodoList={addTodoToTodoList} />
      <TableList list={todoList} />
      </div>
    </Layout>
  )
}




export default App
