import React, { useState } from "react";
import "./Form.css";

const Form = ({ addTodoToTodoList }) => {
  const [inputValue, setInputValue] = useState("");

  
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!inputValue) return;
    addTodoToTodoList(inputValue);
    setInputValue("");
  };

 
  return (
    <form onSubmit={handleSubmit} className="form-container">
      
      <input value={inputValue} onChange={handleInputChange} type="text" placeholder="name"/>
      <input value={inputValue} onChange={handleInputChange} type="number" placeholder="age" />
      <select >
        <option value="Lion">Lion</option>
        <option value="Wolf">Wolf</option>
        <option value="Monky">Monky</option>
        <option value="Snake">Snake</option>
        <option value="Bird">Bird</option>
      </select>
      <input value={inputValue} onChange={handleInputChange} type="src" placeholder="image url" />

      <button type="submit">Add</button>
      
    </form>
  );
};

export default Form;
