import React, { useState } from "react";
import "./Form.css";

const Form = ({ addTodoToTodoList }) => {
  const [nameValue, setnameValue] = useState("");
  const [ageValue, setageValue] = useState("");
  const [typeValue, settypeValue] = useState("");
  const [imageValue, setimageValue] = useState("");


  
  const handleInputChange = (event) => {
    setnameValue(event.target.value);
    setageValue(event.target.value);
    settypeValue(event.target.value);
    setimageValue(event.target.value);

  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!nameValue) return;
    addTodoToTodoList(nameValue);
    setnameValue("");
    addTodoToTodoList(ageValue);
    setageValue("");
    addTodoToTodoList(typeValue);
    settypeValue("");
    addTodoToTodoList(imageValue);
    setimageValue("");
  };

 
  return (
    <form onSubmit={handleSubmit} className="form-container"> 
      <input value={nameValue} onChange={handleInputChange} type="text" placeholder="name"/>
      <input value={ageValue} onChange={handleInputChange} type="number" placeholder="age" />
      <select >
        <option value="Lion">Lion</option>
        <option value="Wolf">Wolf</option>
        <option value="Monky">Monky</option>
        <option value="Snake">Snake</option>
        <option value="Bird">Bird</option>
      </select>
      <input value={imageValue} onChange={handleInputChange} type="src" placeholder="image url" />
      <button type="submit">Add</button>
    </form>


  );
};

export default Form;
