import React, { useState } from "react";
import "./Form.css";

const Form = ({ addTodoToTodoList }) => {
  const [nameValue, setnameValue] = useState("");
  const [ageValue, setageValue] = useState("");
  console.log("hn",nameValue,ageValue);

  const handleNameChange = (event) => {
    console.log("event",event);
    console.log("event targert",event.target.namevalue)
    setnameValue(event.target.namevalue);
    setageValue(event.target.agevalue);
    
  };

  //const handleAgeChange = (event) => {
  //  setageValue(event.target.value);
   // console.log(nameValue,ageValue);
  //};

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!nameValue) return;
    addTodoToTodoList(nameValue,ageValue);
    setnameValue("");
    setageValue("");
    
   
  };

 
  return (
    <form onSubmit={handleSubmit} className="form-container"> 
      <input namevalue={nameValue}  onChange={handleNameChange} type="text" placeholder="name"/>
      <input agevalue={ageValue}  onChange={handleNameChange} type="text" placeholder="age" />
     
      <button type="submit">Add</button>
    </form>


  );
};

export default Form;


