import React ,{useState} from "react";
import Layout from "../layout/Layout";
import Form from "../components/Form/Form";
import PictureUP from "../components/PictureUp/PictureUp";
import "../style/global.css";
import{v4 as uuid} from "uuid";

const App = ()=> {
  const [PictureData,setTodoList]=useState([
    {id: uuid() , name:"name l", age:"2", type:"lion"},
  ]);


  const PictureDataUpLoad= (todo) =>{
    const newTodo={
    id:uuid(),
    name:todo,
    age:todo,
   };
   setTodoList(prevTodoList => [...prevTodoList,newTodo]);
  };

  return (
    <Layout>
      <div className='app-main-container'>
      <Form PictureData={PictureDataUpLoad}/>
      <PictureUP list={PictureData}>picture</PictureUP>
      </div>
    </Layout>
  )
}

export default App
